INSERT INTO Human (role, nom, prenom, bio, url_photo) Values ("ACTOR", "Motensen", "Viggo", "Viggo Mortensen est un acteur, musicien, photographe et peintre américano-danois, né le 20 octobre 1958 à New York.<br>De son rôle dans The Indian Runner de Sean Penn à celui de La Route de John Hillcoat, il est plébiscité dans une quarantaine de films, notamment Portrait de femme de Jane Campion, L'Impasse de Brian De Palma, Meurtre parfait d'Andrew Davis, ou Le Choix d'une vie (A Walk on the Moon) de Tony Goldwyn.<br>Mais c'est la trilogie du Seigneur des Anneaux de Peter Jackson qui le révèle au monde entier grâce à l'incarnation du personnage d'Aragorn, puis trois collaborations avec David Cronenberg — A History of Violence, Les Promesses de l'ombre et A Dangerous Method —, qui lui valent une large reconnaissance critique.", "viggo");
